import { createContext } from "react";

const initialState = {
    cartList: [],
    total: 0
}

const CartContext = createContext(initialState);

export const CartProvider = ({children}) => {

    const value = {};

    return (
        <CartContext.Provider value={value}>
            {children}
        </CartContext.Provider>
    );
}
